import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  background-color: #000;
  color: #fff;
  text-align: center;
  padding: 50px 20px;
`;

const Title = styled.h2`
  font-size: 2.5em;
  margin: 0;
`;

const Subtitle = styled.p`
  font-size: 1.5em;
  margin: 10px 0;
`;

const ThemesGrid = styled.div`
  display: flex;
  justify-content: center;
  gap: 20px;
  margin-top: 30px;
`;

const ThemeCard = styled.div`
  background-color: #fff;
  color: #000;
  border-radius: 10px;
  overflow: hidden;
  width: 200px;
  text-align: left;
`;

const ThemeImage = styled.img`
  width: 100%;
  height: auto;
`;

const ThemeInfo = styled.div`
  padding: 10px;
`;

const ThemeTitle = styled.h3`
  font-size: 1.2em;
  margin: 0;
`;

const ThemeSubtitle = styled.p`
  font-size: 1em;
  color: #555;
`;

const ThemesSection = () => {
  return (
    <Container>
      <Title>Themes. Unlimited themes.</Title>
      <Subtitle>
        Themes in Sleeve make creating and switching between customizations easy. Share your own <br></br>creations with friends and install as many themes as you like with just a double-click.
      </Subtitle>
      <ThemesGrid>
        <ThemeCard>
          <ThemeImage src="https://via.placeholder.com/200" alt="Eternal Sunshine" />
          <ThemeInfo>
            <ThemeTitle>Eternal Sunshine</ThemeTitle>
            <ThemeSubtitle>Forgotten Feels<br/>Slow Magic</ThemeSubtitle>
          </ThemeInfo>
        </ThemeCard>
        <ThemeCard>
          <ThemeImage src="https://via.placeholder.com/200" alt="Bunny Is a Rider" />
          <ThemeInfo>
            <ThemeTitle>Bunny Is a Rider</ThemeTitle>
            <ThemeSubtitle>Caroline Polachek</ThemeSubtitle>
          </ThemeInfo>
        </ThemeCard>
        <ThemeCard>
          <ThemeImage src="https://via.placeholder.com/200" alt="Lost Souls" />
          <ThemeInfo>
            <ThemeTitle>Lost Souls</ThemeTitle>
            <ThemeSubtitle>Baby Keem</ThemeSubtitle>
          </ThemeInfo>
        </ThemeCard>
      </ThemesGrid>
    </Container>
  );
};

export default ThemesSection;
