import React from 'react';
import styled from 'styled-components';
import ThemeImg from '../Images/Themeimg.jpg';
import Artwork1 from '../Images/Artwork1Dark.jpg';
import Artwork2 from '../Images/Artwork2Dark.jpg';
import Artwork3 from '../Images/Artwork3Dark.jpg';

const Container = styled.div`
  display: grid;
  grid-template-columns: 1fr 300px;
  background-color: #000;
  color: #fff;
  padding: 50px 20px;
  height: 80vh; /* Reduced height for centering */
  gap: 20px;
  align-items: center; /* Center items vertically */
  justify-content: center; /* Center items horizontally */
`;

const TextContent = styled.div`
  padding-right: 20px;
  overflow-y: auto;
`;

const ThemeImageContainer = styled.div`
  position: sticky;
  top: 120px;
  width: 100%; /* Make it responsive to its container */
  height: 600px; /* Increased height for larger theme image */
  background-image: url(${ThemeImg});
  background-size: cover;
  background-position: center;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 40px; /* Increased padding for spacing */
  flex-direction: column; /* Arrange related images vertically */
  box-sizing: border-box; /* Ensure padding is included in width and height */
`;

const RelatedImagesContainer = styled.div`
  display: flex;
  flex-direction: column; /* Stack images vertically */
  align-items: center; /* Center images horizontally */
  gap: 20px; /* Space between related images */
`;

const Title = styled.h2`
  font-size: 2.5em;
  margin: 0;
`;

const Subtitle = styled.p`
  font-size: 1.5em;
  margin: 10px 0 30px 0;
`;

const RelatedImage = styled.img`
  width: 100px; /* Increase size for better visibility */
  height: 100px;
  margin: 5px;
  border-radius: 10px; /* Optional: Add border radius for aesthetics */
`;

const CustomizationSection = () => {
  return (
    <Container>
      <TextContent>
        <Title>Artwork</Title>
        <Subtitle>
          Scale artwork all the way up or all the way down. Round the corners or leave them square.<br />
          Choose shadow and lighting effects to bring your album artwork to life.<br />
          Or hide it completely.
        </Subtitle>
        <Title>Typography</Title>
        <Subtitle>
          Pick the track info you want to display, and then exactly how to display it.<br />
          Choose the fonts, weights, sizes, and transparency to use for each line, along with customizing color and shadow.
        </Subtitle>
        {/* Add more content as needed */}
      </TextContent>
      <ThemeImageContainer>
        <RelatedImagesContainer>
          <RelatedImage src={Artwork1} alt="Related" />
          <RelatedImage src={Artwork2} alt="Related" />
          <RelatedImage src={Artwork3} alt="Related" />
        </RelatedImagesContainer>
      </ThemeImageContainer>
    </Container>
  );
};

export default CustomizationSection;
