import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: grid;
  grid-template-columns: 1fr 250px; /* Split the grid into two columns */
  background-color: #000;
  color: #fff;
  padding: 50px 20px;
  height: 100vh; /* Full viewport height */
`;

const TextContent = styled.div`
  padding-right: 20px;
  overflow-y: auto;
`;

const ThemeImageContainer = styled.div`
  position: sticky; /* Make the theme image sticky */
  top: 120px; /* Adjust based on your layout needs */
  height: 300px; /* Adjust based on your layout needs */
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const RelatedImagesContainer = styled.div`
  display: flex;
  justify-content: space-between;
`;

const Title = styled.h2`
  font-size: 2.5em;
  margin: 0;
`;

const Subtitle = styled.p`
  font-size: 1.5em;
  margin: 10px 0 30px 0;
`;

const Image = styled.img`
  width: 100%;
  height: auto;
  object-fit: cover;
`;

const CustomizationSection = () => {
  return (
    <Container>
      <TextContent>
        <Title>Artwork</Title>
        <Subtitle>
          Scale artwork all the way up or all the way down. Round the corners or leave them square.<br/>
          Choose shadow and lighting effects to bring your album artwork to life.<br/>
          Or hide it completely.
        </Subtitle>
        <Title>Typography</Title>
        <Subtitle>
          Pick the track info you want to display, and then exactly how to display it.<br/>
          Choose the fonts, weights, sizes, and transparency to use for each line, along with customizing color and shadow.
        </Subtitle>
        {/* Add more content as needed */}
      </TextContent>
      <ThemeImageContainer>
        <Image src={require('../Images/Themeimg.jpg')} alt="Theme" />
        <RelatedImagesContainer>
          <Image src={require('../Images/Artwork1Dark.jpg')} alt="Related" width="80" height="80" />
          <Image src={require('../Images/Relatedimg2.jpg')} alt="Related" width="80" height="80" />
          <Image src={require('../Images/Relatedimg3.jpg')} alt="Related" width="80" height="80" />
        </RelatedImagesContainer>
      </ThemeImageContainer>
    </Container>
  );
};

export default CustomizationSection;
